def lengste_null_sekvens(nedbør):
    naavaerende_sekvens = 0
    lengste_sekvens = 0

    for nedbørsmengde in nedbør:
        if nedbørsmengde == 0:
            naavaerende_sekvens += 1
            if naavaerende_sekvens > lengste_sekvens:
                lengste_sekvens = naavaerende_sekvens
        else:
            naavaerende_sekvens = 0

    return lengste_sekvens

